function popup() {
	var D = null;
	var A = 0;
	var C = null;
	var B = null;
	popup.prototype.initPopup = function() {
		if (this.shieldingLayer == null) {
			this.shieldingLayer = document.createElement("DIV");
			this.shieldingLayer.id = "mbLayer";
			this.shieldingLayer.className = "mb_layer";
			this.shieldingLayer.style.display = "none";
			document.body.appendChild(this.shieldingLayer)
		}
		if (this.msgBox == null) {
			this.msgBox = document.createElement("DIV");
			this.msgBox.id = "msgBox";
			this.msgBox.className = "wp_layer";
			this.msgBox.style.display = "none";
			this.msgBox.style.width = "415px";
			this.msgBox.style.marginTop = "-88px";
			this.msgBox.style.marginLeft = "-157px";
			document.body.appendChild(this.msgBox)
		}
		if (this.loadingLayer == null) {
			this.loadingLayer = document.createElement("DIV");
			this.loadingLayer.id = "loadingLayer";
			this.loadingLayer.className = "loading";
			this.loadingLayer.style.position = "absolute";
			this.loadingLayer.style.top = "50%";
			this.loadingLayer.style.left = "50%";
			this.loadingLayer.style.display = "none";
			document.body.appendChild(this.loadingLayer)
		}
	};
	popup.prototype.openShieldingLayer = function() {
		A++;
		this.shieldingLayer.style.zIndex = (100 + (A - 1) * 2);
		this.shieldingLayer.style.display = ""
	};
	popup.prototype.closeShieldingLayer = function() {
		A--;
		if (A <= 0) {
			this.shieldingLayer.style.display = "none"
		} else {
			this.shieldingLayer.style.zIndex = (100 + (A - 1) * 2)
		}
	};
	popup.prototype.openFormLayer = function(E) {
		this.initPopup();
		this.openShieldingLayer();
		document.getElementById(E).style.zIndex = (101 + (A - 1) * 2);
		document.getElementById(E).style.display = ""
	};
	popup.prototype.closeFormLayer = function(E) {
		document.getElementById(E).style.display = "none";
		this.closeShieldingLayer()
	};
	popup.prototype.openInfoBox = function(F, G, E) {
		this.initPopup();
		this.openShieldingLayer();
		if (F == null || F == "") {
			F = "信息"
		}
		this.msgBox.innerHTML = "<div class='lay_hd'> 		    	<strong class='lay_tit'>"
				+ F
				+ "</strong> 				<a href=\"javascript:popup.closeBox();\" class='lay_off' hidefocus='true'><i>&times;</i></a> 			</div> 		    <div class='lay_bd'> 		    	<div class='lay_msg'> 		        	 "
				+ G
				+ " 		        </div> 		    </div> 		    <div class='lay_ft'> 		        <a href=\"javascript:popup.closeBox();"
				+ E
				+ ";\" class='sbtn_01_min'><span>确定</span></a> 		    </div>";
		this.msgBox.style.zIndex = (101 + (A - 1) * 2);
		this.msgBox.style.display = ""
	};
	popup.prototype.openConfirmBox = function(G, H, F, E) {
		this.initPopup();
		this.openShieldingLayer();
		if (G == null || G == "") {
			G = "确认"
		}
		this.msgBox.innerHTML = "<div class='lay_hd'> 		    	<strong class='lay_tit'>"
				+ G
				+ "</strong> 				<a href=\"javascript:popup.closeBox();\" class='lay_off' hidefocus='true'><i>&times;</i></a> 			</div> 		    <div class='lay_bd'> 		    	<div class='lay_msg'> 		        	 "
				+ H
				+ " 		        </div> 		    </div> 		    <div class='lay_ft'> 		        <a href=\"javascript:popup.closeBox();"
				+ F
				+ ";\" class='sbtn_01_min'><span>确认</span></a> 		        <a href=\"javascript:popup.closeBox();"
				+ E
				+ ";\" class='sbtn_04_min'><span>取消</span></a> 		    </div>";
		this.msgBox.style.zIndex = (101 + (A - 1) * 2);
		this.msgBox.style.display = ""
	};
	popup.prototype.openUpdateBox = function(G, H, F, E) {
		this.initPopup();
		this.openShieldingLayer();
		if (G == null || G == "") {
			G = "修改"
		}
		this.msgBox.innerHTML = "<div class='lay_hd'> 		    	<strong class='lay_tit'>"
				+ G
				+ "</strong> 				<a href=\"javascript:popup.closeBox();\" class='lay_off' hidefocus='true'><i>&times;</i></a> 			</div> 		    <div class='lay_bd'> 		    	<div class='lay_msg'> 		        	 <div class='text'><textarea name='updateContent' id='updateContent' rows='5' cols='40'></textarea></div> <span class='label'>&nbsp;</span>	<div class='text'>【<input class='inp' type='text' id='updateSign' name='updateSign' style='width: 257px;'></input>】</div>	 <span class='label'>可选内容：&nbsp;</span><input type='checkbox' name='isAddSuffixUpdate' id='isAddSuffixUpdate' >&nbsp;&nbsp;&nbsp;若不想接收该信息请回复PB</input>     </div> 		    </div> 		    <div class='lay_ft'> 		        <a href=\"javascript:popup.closeBox();"
				+ F
				+ ";\" class='sbtn_01_min'><span>确认</span></a> 		        <a href=\"javascript:popup.closeBox();"
				+ E
				+ ";\" class='sbtn_04_min'><span>取消</span></a> 		    </div>";
		this.msgBox.style.zIndex = (101 + (A - 1) * 2);
		this.msgBox.style.display = ""
	};
	popup.prototype.closeBox = function() {
		if (this.msgBox != null) {
			this.msgBox.style.display = "none"
		}
		this.closeShieldingLayer()
	};
	popup.prototype.openLoadingLayer = function() {
		this.initPopup();
		this.openShieldingLayer();
		this.loadingLayer.innerHTML = "<img src='/image/loading.gif' alt='加载中...' />";
		this.loadingLayer.style.zIndex = (101 + (A - 1) * 2);
		this.loadingLayer.style.display = ""
	};
	popup.prototype.closeLoadingLayer = function() {
		if (this.loadingLayer != null) {
			this.loadingLayer.style.display = "none"
		}
		this.closeShieldingLayer()
	}
}
var popup = new popup();